oup">                    <div class="col-lg-12">                        <div> <div class="col-md-4"><div style="float:left">In Time : <span class="modal_in_time">00:00</span></div>   </div>                            <div class="col-md-4"><div style="text-align:center;"> Out Time :<span class="modal_out_time">00:00</span></div></div>                            <div class="col-md-4"><div style="text-align:right;">Work duration: <span class="modal_over_work_time">00:00</span></div></div></div>                        <table class="table table-bordered sortable table-striped table-bordered responsive  no-footer dtr-inline                               tquantity-cntr tamount-right">                            <thead>                                <tr>                                    <th>S.No</th><th>In Time</th><th>Out Time</th><th>Duration</th>                                </tr>                            </thead>                            <tbody id="dynamic_logs_table">                                <tr>                                    <td class="center holiday_class" colspan="4">No logs found...!</td>                                </tr>                            </tbody>                        </table>                    </div>                </div>            </div>        </div>    </div></div><style>    .deactive_class    {        display:none;    }</style><script type="text/javascript">    $(document).ready(function () {        $(".reset").click(function () {            $.ajax({                url: BASE_URL + "api/reset_session/",                type: "POST",                data: {class: ct_class, method: ct_method},                success: function (res)                {                    window.location = window.location.pathname;                }            });        });        //$('#department_select').select2();        var enter_count = "<?= $enter_count ?>";        //alert(enter_count);        if (enter_count == 0)        {            $(".group_check").hide();        } else        {            $("#action").hide();        }        $(".group_check").click(function () {            if ($(this).is(":checked"))            {                $(".single_check").attr("checked", "checked");                $(".attendance_data").removeAttr("disabled");            } else            {                $(".single_check").removeAttr("checked");                $(".attendance_data").attr("disabled", "disabled");            }        });    });    $(document).on('click', '.view_day_log', function () {        var modal_in_time = $(this).closest('tr').find('.time_calc_in_time').text();        var modal_out_time = $(this).closest('tr').find('.time_calc_out_time').text();        var modal_over_work_time = $(this).closest('tr').find('.time_calc_work_time').text();        // if (modal_in_time == null || modal_in_time == '')        //modal_in_time = '-';        //if (modal_out_time == null || modal_out_time == '')        // modal_out_time = '-';        $('.modal_in_time').text('').text(modal_in_time);        $('.modal_out_time').text('').text(modal_out_time);        $('.modal_over_work_time').text('').text(modal_over_work_time);        $('#day_log_modal').modal({            backdrop: 'static',            keyboard: false        });        $('#day_log_modal').modal('show');        var user_id = $(this).attr('user_id');        var date = $(this).attr('date');        $.ajax({            url: BASE_URL + "attendance/reports/get_emp_all_logs_by_day",            type: "POST",            data: {user_id: user_id, created_date: date, modal_in_time: modal_in_time, modal_out_time: modal_out_time},            success: function (res)            {                $('#dynamic_logs_table').html('').html(res);            }        });    });    $(".single_check").live('click', function () {        if ($(this).is(":checked"))        {            $(this).next().removeAttr("disabled");        } else        {            $(this).next().attr("disabled", "disabled");        }    });    $(".group_check,.single_check").live('click', function () {        var count = 0;        var total = 0;        $(".single_check:checked").each(function () {            count++;        });        if (count >= 1)        {            $("#add_attendance").css("display", "block");        } else        {            $("#add_attendance").css("display", "none");        }        $(".single_check").each(function () {            total++;        });        if (count < total)        {            $(".group_check").removeAttr("checked");        }    });    $("#attendance-search").click(function () {        var start_date = $("#start_date").val();        var end_date = $("#end_date").val();        if (start_date == '')        {            alert("Please select start date");            return false;        }    });    $('.print_btn').click(function () {        $(".fulllogs").removeClass("deactive_class");        $(".logs").addClass("deactive_class");        window.print();    });</script>