<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * Education_model * * This model represents education. It operates the following tables: * - education, * * @package	Payroll * @author	Vathsala */class Education_model extends CI_Model {    private $table_name = 'education';    function __construct() {        parent::__construct();    }    /**     * Get all user education     *     * @return	array     */    function get_all_users_education() {        $query = $this->db->get($this->table_name);        if ($query->num_rows() >= 1) {            return $query->result_array();        }        return false;    }    /**     * Get user education by id (user id)     *     * @param	int     * @return	array     */    function get_user_education_by_id($user_id) {        $this->db->where('user_id', $user_id);        $query = $this->db->get($this->table_name);        if ($query->num_rows() >= 1) {            return $query->result_array();        }        return false;    }    /**     * Insert new user education     *     * @param	array     * @param	bool     * @return	id     */    function insert_education($data) {        if ($this->db->insert($this->table_name, $data)) {            $edu_id = $this->db->insert_id();            return $edu_id;        }        return false;    }    /**     * Update user education by user id     *     * @param	array     * @param	int     * @return	bool     */    function update_user_education($user_id, $data) {        $this->db->where('user_id', $user_id);        if ($this->db->update($this->table_name, $data)) {            return true;        }        return false;    }    /**     * Delete user education by user id     *     * @param	int     * @return	bool     */    function delete_user_education($user_id) {        $this->db->where('user_id', $user_id);        $this->db->delete($this->table_name);        if ($this->db->affected_rows() > 0) {            return true;        }        return false;    }}