<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * Family_model * * This model represents family_members. It operates the following tables: * - family_members, * * @package	Payroll * @author	Vathsala */class Increment_model extends CI_Model {    // private $increment_table	= 'increment';    private $increment_table = 'biometric_increments';    function __construct() {        parent::__construct();    }    /**     * Get increment id     *     * @return	array     */    function get_increment_id($type) {        $this->db->where('type', $type);        $query = $this->db->get($this->increment_table);        if ($query->num_rows() == 1) {            return $query->result_array();        }        return false;    }    /* Update increment id */    function update_increment_id($type) {        $last_id = $this->get_increment_id($type);        $last_id = $last_id[0]["last_increment_id"];        $data["last_increment_id"] = preg_replace("|(\d+)|e", "$1+1", $last_id);        $this->db->where('type', $type);        if ($this->db->update($this->increment_table, $data)) {            return true;        }        return false;    }    /* Update id prefix */    function update_increment_prefix($prefix, $suffix) {        $type = 'employee';        $data["last_increment_id"] = $prefix . "-" . $suffix;        $this->db->where('type', $type);       // print_r($data);exit;        if ($this->db->update($this->increment_table, $data)) {            return true;        }        return false;    }}